      "name": "ProcessoLicitatorio",
      "attributes": ["id", "etapa_atual", "data_inicio", "data_fim", "status", "resultado", "valor_contratado"],
      "relationships": [
        {"related_entity": "Edital", "type": "1-1", "cardinality": "exactly one", "constraints": "NOT NULL"},
        {"related_entity": "Proposta", "type": "1-N", "cardinality": "one to many", "constraints": "cascade delete"}
      ]
