        {"related_entity": "Empresa", "type": "N-1", "cardinality": "many to one", "constraints": "NOT NULL"},
        {"related_entity": "ProcessoLicitatorio", "type": "N-1", "cardinality": "many to one", "constraints": "NOT NULL"},
        {"related_entity": "PlanilhaCustos", "type": "1-1", "cardinality": "exactly one", "constraints": "NOT NULL"}
      ]
    },
    {
      "name": "AnaliseViabilidade",
      "attributes": ["id", "score_tecnico", "score_financeiro", "score_documental", "viabilidade_geral", "recomendacao", "data_analise"],
      "relationships": [
        {"related_entity": "Edital", "type": "1-1", "cardinality": "exactly one", "constraints": "NOT NULL"},
        {"related_entity": "Empresa", "type": "N-1", "cardinality": "many to one", "constraints": "NOT NULL"}
      ]
    }
  ],
  "workflows": [
    {
      "id": "WF-001",
      "name": "Processamento de Edital",
      "trigger": "Detecção de novo edital em portal monitorado",
      "steps": [
        "Capturar conteúdo completo do edital",
        "Extrair informações estruturadas (NLP)",
        "Classificar tipo e características",
        "Validar conformidade legal",
        "Calcular viabilidade de participação",
        "Emitir alertas conforme configuração",
        "Armazenar no banco de dados"
      ],
      "decision_points": [
        {"point": "Validação de conformidade", "conditions": ["Conforme", "Não conforme"], "actions": ["Prosseguir análise", "Sinalizar inconformidade"]},
        {"point": "Classificação de viabilidade", "conditions": ["Viável", "Inviável", "Necessita análise manual"], "actions": ["Notificar alta prioridade", "Arquivar", "Encaminhar para analista"]}
      ],
      "end_states": ["Processado e armazenado", "Erro no processamento", "Arquivado por inviabilidade"]
    },
    {
      "id": "WF-002",
      "name": "Análise de Viabilidade",
      "trigger": "Solicitação de análise de viabilidade para edital específico",
      "steps": [
        "Recuperar perfil da empresa",
        "Comparar com requisitos do edital",
        "Validar capacidade técnica",
        "Analisar situação financeira",
        "Verificar documentação disponível",
        "Calcular score de viabilidade",
        "Gerar relatório de análise"
      ],
      "decision_points": [
        {"point": "Capacidade técnica", "conditions": ["Adequada", "Parcialmente adequada", "Inadequada"], "actions": ["Pontuar positivamente", "Aplicar penalidade", "Desclassificar"]},
        {"point": "Situação financeira", "conditions": ["Suficiente", "Limitada", "Insuficiente"], "actions": ["Aprovar", "Requer garantias", "Reprovar"]}
      ],
      "end_states": ["Viável - Recomendar participação", "Inviável - Não participar", "Condicionada - Requer ações específicas"]
    },
    {
      "id": "WF-003",
      "name": "Gestão de Proposta",
      "trigger": "Decisão de participar de licitação",
      "steps": [
        "Montar equipe de proposta",
        "Coletar documentação necessária",
        "Elaborar planilha de custos",
        "Revisar técnica e comercial",
        "Submeter proposta",
        "Acompanhar andamento",
        "Registrar resultado"
      ],
      "decision_points": [
        {"point": "Validação documental", "conditions": ["Completa", "Incompleta"], "actions": ["Prosseguir", "Suspender até complementação"]},
        {"point": "Aprovação comercial", "conditions": ["Aprovada", "Reprovada"], "actions": ["Liberar envio", "Cancelar participação"]}
      ],
      "end_states": ["Proposta enviada", "Proposta cancelada", "Erro no envio"]
    }
  ],
  "glossary": [
    {
      "term": "Pregão Eletrônico",
      "definition": "Modalidade de licitação para aquisição de bens e serviços comuns, realizada preferencialmente de forma eletrônica",
      "context": "Tipos de licitação",
      "synonyms": ["Pregão", "Licitação eletrônica"],"}

